package operators;

public class arithmetic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=15;
		int y=10;
		System.out.println("value "+(x+y)); //addition
		System.out.println("value "+(x-y)); //subtraction
		System.out.println("value "+(x*y)); //multiplication
		System.out.println("value "+(x/y)); //Divison
		System.out.println("value "+(x%y)); //modulo
		System.out.println("total values "+(10*10/5+3-1*4/2)); //All operations

	}

}
