package operators;

public class bitwise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x,y;
		x=15;
		y=10;
		System.out.println(x&y);
		System.out.println(x|y);
		System.out.println(x^y);
		System.out.println(~y);
		System.out.println(x<<y);
		System.out.println(x>>y);
		System.out.println(x>>>y);

	}

}
